INPUT = ''''''
#INPUT='bvwbjplbgvbhsrlpgdmjqwftvncz'

if __name__ == '__main__':
    marker = []
    print(len(INPUT))
    for count, letter in enumerate(INPUT, 1):
        if letter not in marker:
            marker.append(letter)
            if len(marker) >= 14:
                print(count)
                break
        else:
            i = marker.index(letter)
            marker = marker[i+1:]
            marker.append(letter)
